import React from 'react';
import { Box, Flex } from 'rebass';
import './splashPage.css';

const tiles = [
    {
        image: '../../static/data1.jpg',
        caption: 'January 24 - 25th 2020'
    },
    {
        image: '../../static/data2.jpg',
        caption: 'Sign Up'
    },
    {
        image: '../../static/data3.jpg',
        caption: 'Become a Sponsor'
    },
    // {
    //     image: '../../static/data4.jpg',
    //     caption: ''
    // }
]

const Image = (props) => (
    <div className='image'>
        <Box className={`image-box${props.index}`} width={350}>
            <h1 className='image-caption'>{props.caption}</h1>
        </Box>
        <style>{`
            .image-box${props.index} {
                width: 350px;
                height: 200px;
                position: relative;
                z-index: 200;
                overflow: hidden;
            }
            .image-box${props.index}:before {
                content: "";
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
                z-index: -1;
                // background-image: url(${props.image});
                background-position: center;
                background-size: cover;
            }
            .image-box${props.index}:after {
                content: "";
                position: absolute;
                bottom: 0;
                left: 0;
                width: 100%;
                height: 100px;
                z-index: -1;
                background-color: black;
                opacity: 0.5;
                box-shadow: 0 0 10px 10px black;
            }
            .image-caption {
                color: #ffffff;
                position: absolute;
                top: 90px;
                text-align: center;
                width: 100%;
                font-weight: normal;
            }
        `}</style>
    </div>
)

const SplashPage = () => (
    <div className='splashpage'>
        <Flex flexDirection='row' flexWrap='wrap' justifyContent='center'>
            {
                tiles.map(tile => (
                    <Box height={100} key={tile.image}>
                        <Image image={tile.image} index={tiles.indexOf(tile)} caption={tile.caption} />

                    </Box>
                ))
            }
        </Flex>
    </div>
)

export default SplashPage;
