import React from 'react';
import { Box, Flex } from 'rebass';
import Title from '../title/title.js';
import './faqs.css';
import { faqs } from './faqs.json';

class QuestionAnswer extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            extended: false
        }
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        this.setState({ extended: !this.state.extended });
    }

    render() {
        let height = this.state.extended ? (500) : (0);

        const altTheme = {
            backgroundColor: '#2B354C',
            color: '#A7BBCE'
        }
        return (
            <div className='question-container' onClick={this.handleClick} style={this.state.extended ? (altTheme) : (undefined)}>
                <div className='dropdown-question'>
                    {this.props.question}
                </div>
                <div className='dropdown-answer' style={{ maxHeight: height }}>
                    {this.props.answer}
                </div>
            </div>
        )
    }
}

const FAQs = () => {

    const qas = []

    for (let i = 0; i < faqs.length; i++) {
        qas.push(
            <Box width={1} key={`${faqs[i].question.slice(0, 10)}`}>
                <QuestionAnswer question={faqs[i].question} answer={faqs[i].answer} />
            </Box>
        )
    }

    return (
        <div className='faqs-page'>
            <Title title="FAQs" width={[0.5, 0.15]} theme={false} />
            <Flex className='questions' flexDirection='row' flexWrap='wrap' justifyContent='center'>
                {qas}
            </Flex>
        </div>
    )
}

export default FAQs;
