import React from 'react';
import Link from 'next/link';
import { Box, Flex, Image } from 'rebass';
import Title from '../title/title'
import './splashPage.css';

const tiles = [
    {
        image: '../../static/data1.jpg',
        caption: 'Schedule →',
        to: '/schedule'
    },
    {
        image: '../../static/data2.jpg',
        caption: 'Sign Up →',
        href: 'https://docs.google.com/forms/d/e/1FAIpQLSfkF9heSXz8COzuALDl-ihmkZcZI94wphnvnhWmc8W77KJL0A/viewform?fbclid=IwAR2h6Bn-58m-thXqp9VhOD-TcK0dSgCry29_yoTwO-gyIYjiDaonKgD-ZOw'
    },
    {
        image: '../../static/data3.jpg',
        caption: 'Become a Sponsor →',
        href: "../../static/SponsorshipPacket.pdf"
    },
    {
        image: '../../static/data4.jpg',
        caption: 'Facebook Event →',
        href: 'https://www.facebook.com/events/2614689505291723/'
    }
]

const CoolImage = (props) => (
    <div className='image'>
        <Box className={`image-box${props.index}`} width={350}>
            {
                props.to !== undefined ? (
                    <Link href={props.to}>
                        <div className='image-caption-container'>
                            <h1 className='image-caption'>{props.caption}</h1>
                        </div>
                    </Link>
                ) : (
                        <a href={props.href}>
                            <div className='image-caption-container'>

                                <h1 className='image-caption'>{props.caption}</h1>
                            </div>
                        </a>
                    )
            }
        </Box>
        <style>{`
            .image-box${props.index} {
                width: 350px;
                height: 200px;
                position: relative;
                z-index: 200;
                overflow: hidden;
                margin: 20px;
            }
            .image-box${props.index}:before {
                content: "";
                position: absolute;
                top: 0;
                left: 0;
                width: 100%;
                height: 100%;
                z-index: -1;
                background-image: url(${props.image});
                background-position: center;
                background-size: cover;
                transition: 0.3s ease-in-out;
            }
            .image-box${props.index}:after {
                content: "";
                position: absolute;
                bottom: 0;
                left: 0;
                width: 100%;
                height: 100px;
                z-index: -1;
                background-color: black;
                opacity: 0.5;
                box-shadow: 0 0 20px 20px black;
                transition: 0.3s ease-in-out;
            }
            .image-caption-container {
                height: 100%;
            }
            .image-caption {
                color: white;
                position: absolute;
                top: 90px;
                text-align: center;
                width: 100%;
                font-weight: normal;
                transition: 0.3s ease-in-out;
            }
            .image-box${props.index}:hover:before {
                transform: scale(1.3);
            }
            .image-box${props.index}:hover:after {
                height: 100%;
                opacity: 0.8
            }
            .image-box${props.index}:hover .image-caption {
                top: 50px;
            }
            .image-caption-container:hover {
                cursor: pointer;
            }
        `}</style>
    </div>
)

const SplashPage = () => (
    <div className='splashpage'>
        <Box width={[1, 0.7]} ml='auto' mr='auto'>
            <Title title="Quick Links" width={[0.8, 0.4]} />
            <Flex className="quick-links" flexDirection='row' flexWrap='wrap' justifyContent='center'>
                <a><h3>Devpost</h3></a>
                <a href="https://piazza.com/class/k58n0jdmc721zx"><h3>Piazza</h3></a>
                <a><h3>Slack</h3></a>
                <a href="https://docs.google.com/presentation/d/1TlqM4wdSpreIJPW0M-eEt-kRJRZ1_yGtwdIVXcCUE5M/edit?usp=sharing"><h3>Opening Presentation</h3></a>
            </Flex>

            <Flex flexDirection='row' flexWrap='wrap' justifyContent='center'>
                {
                    tiles.map(tile => (
                        <CoolImage image={tile.image} index={tiles.indexOf(tile)} caption={tile.caption} to={tile.to} href={tile.href} key={tile.image} />
                    ))
                }
            </Flex>
            <Title title="Thank you to our Sponsors!" width={[1, 0.6]} />
            <Box width={[0.9, 0.8]} ml='auto' mr='auto'>
                <h2 style={{ fontWeight: "normal", color: "#2B354C" }}>Gold Sponsors</h2>
                <Flex justifyContent="center" flexWrap="wrap" flexDirection="row">
                    <Box width={340} mt={30}>
                        <Image src="../../static/bbva.png" alt="" mt={35} />
                    </Box>
                    <Box width={180} m={10}>
                        <Image src="../../static/d2k.png" alt="" />
                    </Box>
                    <Box width={300} mt={80} mx={40} mb={40}>
                        <Image src="../../static/exxonmobil.png" alt="" />
                    </Box>
                    <Box width={280} my={40}>
                        <Image src="../../static/bill.svg" alt="" />
                    </Box>
                    <Box width={240}>
                        <Image src="../../static/nov.png" alt="" />
                    </Box>

                </Flex>
                <h2 style={{ fontWeight: "normal", color: "#2B354C" }}>Silver Sponsors</h2>
                <Flex justifyContent="center" flexWrap="wrap" flexDirection="row">
                    <Box width={180}>
                        <Image src="../../static/chevron.svg" alt="" />
                    </Box>
                    <Box width={200}>
                        <Image src="../../static/accenture.png" alt="" />
                    </Box>
                </Flex>
            </Box>
        </Box>

    </div >
)

export default SplashPage;
